mapper.select:
SELECT id, create_date AS createDate, del_flag AS delFlag, univer_logo AS univerLogo, univer_name AS univerName, cooperation_profile AS cooperationProfile, reply,author,create_date as createDate FROM qst_university_accreditation WHERE del_flag = '0'
ORDER BY create_date DESC
limit #{start},#{size}

mapper.update:
update qst_university_accreditation  SET univer_logo =#{univerLogo},
univer_name=#{univerName},cooperation_profile=#{cooperationProfile} ,
reply=#{reply},author=#{author}
where id=#{id}
